import React, { useState } from 'react';

function App() {
  const [formData, setFormData] = useState({
    underlying_price: '',
    strike_price: '',
    time_to_expiry: '',
    volatility: '',
    risk_free_rate: ''
  });
  const [result, setResult] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await fetch('http://127.0.0.1:8000/api/black-scholes/', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });
      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  return (
    <div>
      <h1>Black-Scholes Option Pricing Calculator</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="underlying_price"
          value={formData.underlying_price}
          onChange={handleChange}
          placeholder="Underlying Price"
        />
        <input
          type="text"
          name="strike_price"
          value={formData.strike_price}
          onChange={handleChange}
          placeholder="Strike Price"
        />
        <input
          type="text"
          name="time_to_expiry"
          value={formData.time_to_expiry}
          onChange={handleChange}
          placeholder="Time to Expiry"
        />
        <input
          type="text"
          name="volatility"
          value={formData.volatility}
          onChange={handleChange}
          placeholder="Volatility"
        />
        <input
          type="text"
          name="risk_free_rate"
          value={formData.risk_free_rate}
          onChange={handleChange}
          placeholder="Risk-Free Rate"
        />
        <button type="submit">Calculate</button>
      </form>

      {result && (
        <div>
          <p>Call Option Price: {result.call_option_price}</p>
          <p>Put Option Price: {result.put_option_price}</p>
        </div>
      )}
    </div>
  );
}

export default App;
