import React, { useState } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    S: '',
    K: '',
    T: '',
    r: '',
    sigma: '',
    option_type: 'call'
  });

  const [optionPrice, setOptionPrice] = useState(null);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://127.0.0.1:5000/api/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    });

    const data = await response.json();
    setOptionPrice(data.option_price);
  };

  return (
    <div className="container">
      <h1>Black-Scholes Option Pricing Calculator</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Stock Price (S₀):
          <input
            type="number"
            name="S"
            value={formData.S}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Strike Price (K):
          <input
            type="number"
            name="K"
            value={formData.K}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Time to Maturity (T) (in years):
          <input
            type="number"
            name="T"
            step="0.01"
            value={formData.T}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Risk-free Rate (r):
          <input
            type="number"
            name="r"
            step="0.01"
            value={formData.r}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Volatility (σ):
          <input
            type="number"
            name="sigma"
            step="0.01"
            value={formData.sigma}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Option Type:
          <select
            name="option_type"
            value={formData.option_type}
            onChange={handleChange}
          >
            <option value="call">Call</option>
            <option value="put">Put</option>
          </select>
        </label>

        <button type="submit">Calculate</button>
      </form>

      {optionPrice !== null && (
        <div className="result">
          <h2>Calculated {formData.option_type} Option Price: {optionPrice}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
