import React, { useState } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    S: '',
    K: '',
    T: '',
    r: '',
    sigma: ''
  });

  const [callOptionPrice, setCallOptionPrice] = useState(null);
  const [putOptionPrice, setPutOptionPrice] = useState(null);

  // New state for dark mode
  const [isDarkMode, setIsDarkMode] = useState(false);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const response = await fetch('http://127.0.0.1:5000/api/calculate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(formData)
    });

    const data = await response.json();
    setCallOptionPrice(data.call_price);
    setPutOptionPrice(data.put_price);
  };

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
  };

  return (
    <div className={`container ${isDarkMode ? 'dark' : ''}`}>
      <h1>Black-Scholes Option Calculator</h1>

      {/* Button to toggle dark mode */}
      <button onClick={toggleDarkMode}>
        {isDarkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
      </button>

      <form onSubmit={handleSubmit}>
        <label>
          Stock Price (S₀):
          <input
            type="number"
            name="S"
            value={formData.S}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Strike Price (K):
          <input
            type="number"
            name="K"
            value={formData.K}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Time to Maturity (T in years):
          <input
            type="number"
            name="T"
            step="0.01"
            value={formData.T}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Risk-free Rate (r):
          <input
            type="number"
            name="r"
            step="0.01"
            value={formData.r}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Volatility (σ):
          <input
            type="number"
            name="sigma"
            step="0.01"
            value={formData.sigma}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit">Calculate</button>
      </form>

      {callOptionPrice !== null && putOptionPrice !== null && (
        <div className="result">
          <h2>Call Option Price: {callOptionPrice}</h2>
          <h2>Put Option Price: {putOptionPrice}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
