import React, { useState } from 'react';
import './App.css';

function App() {
  const [formData, setFormData] = useState({
    S: '',
    K: '',
    T: '',
    r: '',
    sigma: ''
  });

  const [callOptionPrice, setCallOptionPrice] = useState(null);
  const [putOptionPrice, setPutOptionPrice] = useState(null);
  const [errorMessage, setErrorMessage] = useState(null);  // For error handling

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(null);  // Reset error message before new request

    try {
      const response = await fetch('http://127.0.0.1:5000/api/calculate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(formData)
      });

      if (!response.ok) {
        throw new Error(`Server error: ${response.status}`);
      }

      const data = await response.json();

      // Check if response contains the required data
      if (data.call_price === undefined || data.put_price === undefined) {
        throw new Error('Incomplete data from server');
      }

      setCallOptionPrice(data.call_price);
      setPutOptionPrice(data.put_price);
    } catch (error) {
      console.error('Error fetching data:', error);
      setErrorMessage('Failed to fetch option prices. Please try again.');
    }
  };

  return (
    <div className="container">
      <h1>Black-Scholes Option Calculator</h1>
      <form onSubmit={handleSubmit}>
        <label>
          Stock Price (S₀):
          <input
            type="number"
            name="S"
            value={formData.S}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Strike Price (K):
          <input
            type="number"
            name="K"
            value={formData.K}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Time to Maturity (T in years):
          <input
            type="number"
            name="T"
            step="0.01"
            value={formData.T}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Risk-free Rate (r):
          <input
            type="number"
            name="r"
            step="0.01"
            value={formData.r}
            onChange={handleChange}
            required
          />
        </label>

        <label>
          Volatility (σ):
          <input
            type="number"
            name="sigma"
            step="0.01"
            value={formData.sigma}
            onChange={handleChange}
            required
          />
        </label>

        <button type="submit">Calculate</button>
      </form>

      {errorMessage && <div className="error-message">{errorMessage}</div>}  {/* Display error */}

      {callOptionPrice !== null && putOptionPrice !== null && (
        <div className="result">
          <h2>Call Option Price: {callOptionPrice}</h2>
          <h2>Put Option Price: {putOptionPrice}</h2>
        </div>
      )}
    </div>
  );
}

export default App;
